troEscribano = new java.util.ArrayList();\n        parametrosfnRegistroEscribano.add(comp_TXT_numeroRegistro.getValue().trim()); //escribano / String\n        new modelos.builder.eventos.ModeloEventos().ejecutarFuncion(\"fnRegistroEscribano\",parametrosfnRegistroEscribano);\n        Map return16mapa=(Map) ((org.zkoss.zul.Window) org.zkoss.zk.ui.Executions.getCurrent().getDesktop().getAttribute(\"winBuilder\")).getAttribute(\"return16mapa\");\n\t\t\n            if(return16mapa.get(\"items\").size()==0){ \n                Clients.showNotification(\"Introduzca un número de registro valido.\", \"error\", comp_TXT_numeroRegistro, \"\", 3100);\n                comp_TXT_numeroRegistro.setFocus(true);\n                comp_TXT_numeroRegistro.setStyle(comp_TXT_numeroRegistro.getStyle() + \"; border-color: red\");\n                comp_TXT_numeroRegistro.setValue(\"-\");\n                comp_TXT_nombreEscribano.setValue(\"-\");\n                comp_TXT_apellidoEscribano.setValue(\"-\");\n                comp_TXT_domicilioEscribano.setValue(\"-\");\n                comp_TXT_idEscribano.setValue(\"-\");\n                comp_TXT_emailEscribano.setValue(\"-\");  \n            }else{\n                comp_TXT_numeroRegistro.setStyle(comp_TXT_numeroRegistro.getStyle() + \"; border-color: #606060\");\n                String apellido = return16mapa.get(\"items\").get(0).get(\"mes_apellido\");\n                String nombre = return16mapa.get(\"items\").get(0).get(\"mes_nombre\");\n                String domicilio = return16mapa.get(\"items\").get(0).get(\"mes_domicilio\");\n                String id = return16mapa.get(\"items\").get(0).get(\"mes_id\");\n\n                String email = return16mapa.get(\"items\").get(0).get(\"mes_email\");\n\n                if(email==null || email==\"\"){\n                    \n                }else{\n                    comp_TXT_emailEscribano.setValue(email);\n                }\n\n                if(apellido==null || apellido==\"\" || nombre==null || nombre==\"\" || domicilio==null || domicilio==\"\" || id==null || id==\"\"){\n                    Messagebox.show(\"Datos incompletos, por favor comunicarse con el Área Técnica\",\"Advertencia\",null,null,null);\n                }\n\n                comp_TXT_nombreEscribano.setValue(nombre);\n                comp_TXT_apellidoEscribano.setValue(apellido);\n                comp_TXT_domicilioEscribano.setValue(domicilio);\n                comp_TXT_idEscribano.setValue(id);\n              \n            }\n\n        } catch (Exception e) {\n            Messagebox.show(\"Servicio no disponible por el momento. Por favor intente mas tarde.\",\"Advertencia\",null,null,null);\n            comp_TXT_nombreEscribano.setValue(\"-\");\n            comp_TXT_apellidoEscribano.setValue(\"-\");\n            comp_TXT_domicilioEscribano.setValue(\"-\");\n            comp_TXT_idEscribano.setValue(\"-\");\n            comp_TXT_emailEscribano.setValue(\"-\");\n        }\n}\n\n\n/*\n    \n    \nif(!matcherOrden.matches()){\n    Clients.showNotification(\"Cargue correctamente el apellido del Transmitente.\", \"error\", comp_TXT_nombreTramitente3, \"\", 3100);\n\tcomp_TXT_nombreTramitente3.setFocus(true);\n\tcomp_TXT_nombreTramitente3.setStyle(comp_TXT_nombreTramitente3.getStyle() + \"; border-color: red\");\n} else {\n    comp_TXT_nombreTramitente3.setStyle(comp_TXT_nombreTramitente3.getStyle() + \"; border-color: #606060\");\n}*/','Escribania','1.4.9') ;
