INSERT INTO eventos (COMPONENTES_ID_FK,TIPOEVENTOS_ID_FK,CODIGO,PROCESO,VERSION)   VALUES(@vIdComponentes,7,'import java.util.regex.Matcher;\nimport java.util.regex.Pattern;\nimport org.zkoss.zul.Messagebox;\nimport org.zkoss.zul.Textbox;\nimport org.zkoss.zul.Checkbox;\nimport org.zkoss.zk.ui.util.Clients;\nimport modelos.builder.componentes.*;\n\nModeloComponente sup = new ModeloComponente();\n\nTextbox comp_TXT_emailEscribano=(Textbox) sup.getInstanciaComponente(\"comp_TXT_emailEscribano\");\n\n// Chequeo de correcto ingreso de mail\nPattern pattern = Pattern.compile(\"^[_A-Za-z0-9-\\\\+]+(\\\\.[_A-Za-z0-9-]+)*@\" + \"[A-Za-z0-9-]+(\\\\.[A-Za-z0-9]+)*(\\\\.[A-Za-z]{2,})$\");\nString mail = comp_TXT_emailEscribano.getValue().toString();\nMatcher mather = pattern.matcher(mail);\n\nif (!mather.find() == true) \n{\n    Clients.showNotification(\"Ingrese un correo válido.\", \"error\", comp_TXT_emailEscribano, \"\", 3100);\n\tcomp_TXT_emailEscribano.setFocus(true);\n\tcomp_TXT_emailEscribano.setStyle(comp_TXT_emailEscribano.getStyle() + \"; border-color: red\");\n    comp_TXT_emailEscribano.setValue(\"-\");\n} else {\n    comp_TXT_emailEscribano.setStyle(comp_TXT_emailEscribano.getStyle() + \"; border-color: #606060\"); //#4BB543 Green Success\n}','Escribania','1.4.9') ;
