sible(false);\n        dis_planchaATM1.setVisible(false);\n        cont_requisitosTramitess.setVisible(true);\n    }else{\n                    \n        dis_planoDigitalizado.setVisible(false);  \n        dis_planoDigitalizado1.setVisible(false); \n        dis_planchaATM.setVisible(false);\n        dis_planchaATM1.setVisible(false);\n        cont_requisitosTramitess.setVisible(false);\n    }\n    \n}\nelse if(comp_CMB_tramite.getSelectedIndex() == 2){\n    \n    if(comp_IN_subParcelaCUT.getValue()==0){\n        dis_planoDigitalizado.setVisible(true); \n        dis_planoDigitalizado1.setVisible(true);\n        dis_planchaATM.setVisible(false);\n        dis_planchaATM1.setVisible(false);\n        cont_requisitosTramitess.setVisible(true);\n    }else{\n        dis_planoDigitalizado.setVisible(false);    \n        dis_planoDigitalizado1.setVisible(false);\n        dis_planchaATM.setVisible(false);\n        dis_planchaATM1.setVisible(false);\n        cont_requisitosTramitess.setVisible(false);\n    }\n}\n\n\n\n/*\n//SI SE COMPLETA SECTOR DE NC SE ANULA LA DE NOMENCLATURA GEOFERENCIADA !=\nif(!comp_IN_manzanaCut.getValue().equals(0) || !comp_IN_sectorCUT.getValue().equals(0) || !comp_IN_subParcelaCUT.getValue().equals(0)  || !comp_IN_parcelaCUT.getValue().equals(0) || !comp_IN_DVCata.getValue().equals(0)  ){\n    dis_nomCatastralGeoreferenciada.setVisible(false);\n\tdis_departamento_GEO.setVisible(false);\n    //Messagebox.show(\"NOTA: Para el caso de que cargue la NOMENCLATURA CATASTRAL TRADICIONAL NO PODRA CARGAR LA NOMENCLATURA GEOREFERENCIADA, por favor valide la información con su documentación.\",\"Advertencia\",null,null,null);\n}else{\n    dis_nomCatastralGeoreferenciada.setVisible(true);\n\tdis_departamento_GEO.setVisible(true);\n}*/\n\n\n\n\n','Escribania','1.4.9') ;
