INSERT INTO eventos (COMPONENTES_ID_FK,TIPOEVENTOS_ID_FK,CODIGO,PROCESO,VERSION)   VALUES(@vIdComponentes,7,'import java.util.regex.Matcher;\nimport java.util.regex.Pattern;\nimport org.zkoss.zk.ui.util.Clients;\nimport org.zkoss.zul.*;\nimport modelos.builder.componentes.*;\n\nModeloComponente sup = new ModeloComponente();\n\nTextbox comp_TXT_nombreTramitente1=(Textbox) sup.getInstanciaComponente(\"comp_TXT_nombreTramitente1\");\nString patronOrden = \"[\\\\w\\\\s]{1,70}\"; // compara numero de 0-9 y tiene una entrada de 1 o tres caracteres\nString orden = comp_TXT_nombreTramitente1.getValue(); // obtiene el el valor de la variable. Cambiar comp_TXT_bdOrden por la variable\nPattern patternOrden = Pattern.compile(patronOrden);//modificar nombre\nMatcher matcherOrden = patternOrden.matcher(orden);//modificar nombre\n\nString nombre = comp_TXT_nombreTramitente1.getValue().trim();\nnombre = nombre.toUpperCase();\ncomp_TXT_nombreTramitente1.setValue(nombre);\n\nif(!matcherOrden.matches()){\n    Clients.showNotification(\"Cargue correctamente el nombre del Transmitente.\", \"error\", comp_TXT_nombreTramitente1, \"\", 3100);\n\tcomp_TXT_nombreTramitente1.setFocus(true);\n\tcomp_TXT_nombreTramitente1.setStyle(comp_TXT_nombreTramitente1.getStyle() + \"; border-color: red\");\n} else {\n    comp_TXT_nombreTramitente1.setStyle(comp_TXT_nombreTramitente1.getStyle() + \"; border-color: #606060\");\n}','Escribania','1.4.9') ;
