;\n    String cuit = \"\";\n\n\n        if (comp_TXT_personaAdquirente1DNI.getValue().trim().equals(\"-\")) {\n            Messagebox.show(\"Por favor, cargue el CUIT del primer Adquirente\", \"Advertencia\", null, null, null);\n        } else if (!matcherCUIL.matches()) {\n            Messagebox.show(\"Por favor, ingrese los once dígitos del CUIT\", \"Advertencia\", null, null, null);\n            comp_TXT_personaAdquirente1DNI.setValue(\"-\");\n        } else {\n            cuit = dato;\n            //la secuencia de valores de factor es 5, 4, 3, 2, 7, 6, 5, 4, 3, 2\n            int factor;\n            factor = 5;\n            int[] c = new int[11];\n            int resultado = 0;\n\n            int ultimoint = Integer.parseInt(Character.toString(cuit.charAt(cuit.length() - 1)));\n\n            for (int i = 0; i < 10; i++) {\n\n                c[i] = Integer.parseInt(Character.toString(cuit.charAt(i)));\n                resultado = resultado + c[i] * factor;\n                factor = (factor == 2) ? 7 : factor - 1;\n                System.out.println(resultado);\n            }\n\n            int control = 0;\n            int ver = (resultado % 11);\n            if (ver == 0) {\n                control = ver;\n            } else {\n                control = 11 - ver;\n\n            }\n            if (control == ultimoint) {\n                cuilerror = false;\n            } else {\n                cuilerror = true;\n            }\n\n            if (cuilerror) {\n                Messagebox.show(\"Por favor, ingrese un CUIT valido\", \"Advertencia\", null, null, null);\n                comp_TXT_personaAdquirente1DNI.setValue(\"-\");\n            } else {\n\n            }\n\n        }\n\n}\n\n\n\n    switch (itemCombo){\n\n        case \"Consumidor Final\":\n        \n        if(comp_RA_personaFisicaJuridica.getSelectedIndex()==0){ //física\n            validarDNI();\n        }else if(comp_RA_personaFisicaJuridica.getSelectedIndex()==1){ //jurídica\n            validarDNI();\n        }\n        \n        break;\n\n        case \"Exento\":\n            validarCUIT();\n        break;\n\n        case \"Responsable Inscripto\":\n            validarCUIT();\n        break;\n\n        case \"Monotributo\":\n            validarCUIT();\n        break;\n\n        case \"Sujetos No Recategorizados\":\n            validarCUIT();\n        break;\n\n    }','Escribania','1.4.9') ;
