INSERT INTO eventos (COMPONENTES_ID_FK,TIPOEVENTOS_ID_FK,CODIGO,PROCESO,VERSION)   VALUES(@vIdComponentes,2,'import modelos.builder.componentes.*;  \nimport org.zkoss.zul.*;\nimport org.zkoss.zk.ui.util.Clients;\n\nModeloComponente sup = new ModeloComponente();\n\nBandbox comp_BANDBOX_cpPostal=(Bandbox) sup.getInstanciaComponente(\"comp_BANDBOX_cpPostal\");\nTextbox comp_TXT_cpPostal=(Textbox) sup.getInstanciaComponente(\"comp_TXT_cpPostal\");\nListbox comp_GRID_codigoPostal=(Listbox) sup.getInstanciaComponente(\"comp_GRID_codigoPostal\");\nTextbox comp_TXT_idCodigoPostal2=(Textbox) sup.getInstanciaComponente(\"comp_TXT_idCodigoPostal2\");\n\nCombobox comp_CMB_departamentoPostal=(Combobox) sup.getInstanciaComponente(\"comp_CMB_departamentoPostal\");\n\nTextbox comp_TXT_cpPostal=(Textbox) sup.getInstanciaComponente(\"comp_TXT_cpPostal\");\n\nif(comp_CMB_departamentoPostal.getValue().equals(\"-\") || comp_CMB_departamentoPostal.getValue().equals(\" \"))\n{\n   Clients.showNotification(\"Por favor seleccione un Departamento Postal\", \"error\", comp_CMB_departamentoPostal, \"\",3100);\n   comp_CMB_departamentoPostal.setFocus(true);\n   comp_BANDBOX_cpPostal.setValue(\"-\");\n   comp_TXT_cpPostal.setValue(\"-\");\n    \n}else{\n\nString aux=comp_BANDBOX_cpPostal.getValue().trim();\n  \ncomp_TXT_cpPostal.setValue(aux);\n\n\nString insetarVar =\"\";\nString auxi =\"\";\n\n    for (java.util.Iterator it = comp_GRID_codigoPostal.getItems().iterator(); it.hasNext();)//se le cambia el nombre a la grilla que se quiera recorrer\n    {\n\n        org.zkoss.zul.Listitem li = (org.zkoss.zul.Listitem) it.next();\n        auxi=li.getChildren().get(1).getLabel().trim();\n        \n        \n        \n        if(comp_TXT_cpPostal.getValue().trim().equals(auxi)){\n            insetarVar=li.getChildren().get(0).getLabel();\n            \n        }\n        \n        \n    }    \n\n\ncomp_TXT_idCodigoPostal2.setValue(insetarVar);\n\n\n}\n\n\n\n\n\n\n','Escribania','1.4.9') ;
