INSERT INTO eventos (COMPONENTES_ID_FK,TIPOEVENTOS_ID_FK,CODIGO,PROCESO,VERSION)   VALUES(@vIdComponentes,7,'import java.util.regex.Matcher;\nimport java.util.regex.Pattern;\nimport org.zkoss.zul.*;\nimport modelos.builder.componentes.*;\n\nModeloComponente sup = new ModeloComponente();\n\nTextbox comp_TXT_personaAdquirente3DNI = (Textbox) sup.getInstanciaComponente(\"comp_TXT_personaAdquirente3DNI\");\nRadiogroup comp_RA_personaFisicaJuridica=(Radiogroup) sup.getInstanciaComponente(\"comp_RA_personaFisicaJuridica\");\n\nString value = comp_TXT_personaAdquirente3DNI.getValue().trim();\n\n//Verifica que el campo no quede vacio\nif (value.equals(\"\") || value.equals(null)) {\n    comp_TXT_personaAdquirente3DNI.setValue(\"-\");\n}\n\n//Valida Tipo de Persona\nif (comp_RA_personaFisicaJuridica.getSelectedIndex()==0) { //PERSONA FISICA - VALIDA DNI\n    \n    String patronOrden = \"[0-9]{7,8}\"; // compara numero de 0-9 y tiene una entrada de 1 o tres caracteres\n    String orden = comp_TXT_personaAdquirente3DNI.getValue(); // obtiene el el valor de la variable. Cambiar comp_TXT_bdOrden por la variable\n    Pattern patternOrden = Pattern.compile(patronOrden); //modificar nombre\n    Matcher matcherOrden = patternOrden.matcher(orden); //modificar nombre\n\n    //Revisa que se haya ingresado un DNI correcto\n    if (!matcherOrden.matches()) {\n        Messagebox.show(\"Por favor, ingrese correctamente el DNI del tercer Adquirente\", \"Advertencia\", null, null, null);\n        comp_TXT_personaAdquirente3DNI.setValue(\"-\");\n    }\n\n} else if(comp_RA_personaFisicaJuridica.getSelectedIndex()==1){ //PERSONA JURIDICA - VALIDA CUIT\n    \n    String dato = comp_TXT_personaAdquirente3DNI.getValue();\n    dato = dato.replaceAll(\"-\", \"\");\n    String patroncuil = \"[0-9]{11}\";\n    String cuil = dato;\n    Pattern patternCUIL = Pattern.compile(patroncuil);\n    Matcher matcherCUIL = patternCUIL.matcher(cuil);\n    boolean cuilerror = false;\n    String cuit = \"\";\n\n\n        if (comp_TXT_personaAdquirente3DNI.getValue().trim().equals(\"-\")) {\n            //No ha cargado nada\n        } else if (!matcherCUIL.matches()) {\n            Messagebox.show(\"Por favor, ingrese los once dígitos del CUIT\", \"Advertencia\", null, null, null);\n            comp_TXT_personaAdquirente3DNI.setValue(\"-\");\n        } else {\n            cuit = dato;\n            //la secuencia de valores de factor es 5, 4, 3, 2, 7, 6, 5, 4, 3, 2\n            int factor;\n            factor = 5;\n            int[] c = new int[11];\n            int resultado = 0;\n\n            int ultimoint = Integer.parseInt(Character.toString(cuit.charAt(cuit.length() - 1)));\n\n            for (int i = 0; i < 10; i++) {\n\n                c[i] = Integer.parseInt(Character.toString(cuit.charAt(i)));\n                resultado = resultado + c[i] * factor;\n                factor = (factor == 2) ? 7 : factor - 1;\n                System.out.println(resultado);\n            }\n\n            int control = 0;\n            int ver = (resultado % 11);\n            if (ver == 0) {\n                control = ver;\n            } else {\n                control = 11 - ver;\n\n            }\n            if (control == ultimoint) {\n                cuilerror = false;\n            } else {\n                cuilerror = true;\n            }\n\n            if (cuilerror) {\n                Messagebox.show(\"Por favor, ingrese un CUIT valido\", \"Advertencia\", null, null, null);\n                comp_TXT_personaAdquirente3DNI.setValue(\"-\");\n            } else {\n\n            }\n\n        }\n    \n}','Escribania','1.3') ;
