INSERT INTO eventos (COMPONENTES_ID_FK,TIPOEVENTOS_ID_FK,CODIGO,PROCESO,VERSION)   VALUES(@vIdComponentes,2,'import modelos.builder.componentes.*;  \nimport org.zkoss.zul.*;\n  \nModeloComponente sup = new ModeloComponente();\n\nListbox comp_GRID_barrios=(Listbox) sup.getInstanciaComponente(\"comp_GRID_barrios\");\n\nTextbox comp_TXT_idBarrioFisico=(Textbox) sup.getInstanciaComponente(\"comp_TXT_idBarrioFisico\");\n\n\nBandbox comp_BANDBOX_barrio=(Bandbox) sup.getInstanciaComponente(\"comp_BANDBOX_barrio\");\nTextbox comp_TXT_barrioFisico=(Textbox) sup.getInstanciaComponente(\"comp_TXT_barrioFisico\");\n  \nString aux=comp_BANDBOX_barrio.getValue();\n  \ncomp_TXT_barrioFisico.setValue(aux);\n\n\nString valorGrilla=\"\";\nString idActual =\"\";\n\nfor (java.util.Iterator it = comp_GRID_barrios.getItems().iterator(); it.hasNext();)//se le cambia el nombre a la grilla que se quiera recorrer\n    {\n\n        org.zkoss.zul.Listitem li = (org.zkoss.zul.Listitem) it.next();\n\n       valorGrilla=li.getChildren().get(1).getLabel().trim();\n    if(valorGrilla.equals(aux)){\n        idActual=li.getChildren().get(0).getLabel().trim();\n    }\n}\n\ncomp_TXT_idBarrioFisico.setValue(idActual);\n\n','Escribania','1.3') ;
