INSERT INTO eventos (COMPONENTES_ID_FK,TIPOEVENTOS_ID_FK,CODIGO,PROCESO,VERSION)   VALUES(@vIdComponentes,2,'import modelos.builder.componentes.*;  \nimport org.zkoss.zul.*;\n  \nModeloComponente sup = new ModeloComponente();\n\nBandbox comp_BANDBOX_CutPHCalle=(Bandbox) sup.getInstanciaComponente(\"comp_BANDBOX_CutPHCalle\");\nTextbox comp_TXT_CutPHCalle=(Textbox) sup.getInstanciaComponente(\"comp_TXT_CutPHCalle\");\nListbox comp_GRID_CalleUbicacion=(Listbox) sup.getInstanciaComponente(\"comp_GRID_CalleUbicacion\");\nTextbox comp_TXT_CUTPHIDCALLE=(Textbox) sup.getInstanciaComponente(\"comp_TXT_CUTPHIDCALLE\");\n  \nString aux=comp_BANDBOX_CutPHCalle.getValue();\n  \ncomp_TXT_CutPHCalle.setValue(aux);\n\n\n//desde aca\n\n\n\n  \n\nString valorGrilla=\"\";\nString idActual =\"\";\n\nfor (java.util.Iterator it = comp_GRID_CalleUbicacion.getItems().iterator(); it.hasNext();)//se le cambia el nombre a la grilla que se quiera recorrer\n    {\n\n        org.zkoss.zul.Listitem li = (org.zkoss.zul.Listitem) it.next();\n\n       valorGrilla=li.getChildren().get(1).getLabel().trim();\n    if(valorGrilla.equals(aux)){\n        idActual=li.getChildren().get(0).getLabel().trim();\n    }\n}\n\ncomp_TXT_CUTPHIDCALLE.setValue(idActual);\n\n\n','Escribania','1.3') ;
